utton type=\"submit\" class=\"btn btn-info\">検索</button>\n        </form>\n        \n    </div>\n  </nav>\n  \n    <!-- jQuery (necessary for Bootstrap's JavaScript plugins) -->\n    <script src=\"//ajax.googleapis.com/ajax/libs/jquery/2.1.3/jquery.min.js\"></script>\n    <!-- Include all compiled plugins (below), or include individual files as needed -->\n    <script src=\"js/bootstrap.min.js\"></script>\n    \n    <style type=\"text/css\">\n\t<!-- div.s,span.s,div.s img.s{position:absolute;}td,th{font-size:16px;}body{color:white;background:black;padding-top:70px;} -->\n\t</style>\n\n<body>\n\n<div class=\"container-fluid\">\n\n<?php\n// json2score\ninclude_once('table.php');\n\n// isset\nif(isset($_GET[\"md5\"])){\n\t$md5 = $_GET[\"md5\"];\n\t$md5_flag = true;\n}\nelse{\n\t$md5_flag = false;\n}\n\nif(isset($_GET[\"h\"])){\n\t$height = $_GET[\"h\"];\n}\nelse{\n\t$height = 128;\n}\n\nif($md5_flag){\n\tif(!file_exists(\"./json/\".$md5.\".json\")){\n\t\techo \"この譜面はまだ登録されていません。\";\n\t\texit();\n\t}\n\t\n\t$json = file_get_contents(\"./json/\".$md5.\".json\");\n\t$data = json_decode($json, true);\n\t\n\techo '<title>'.$data[\"TITLE\"].'</title>';\n\t\n\tif(isset($_GET[\"dbg\"])){\n\t\techo \"<pre>\";\n\t\techo var_dump($data);\n\t\techo \"</pre>\";\n\t}\n\t\n\tif($data[\"TITLE\"] == null){\n\t\techo \"譜面が壊れているかもしれません。管理者に問い合わせるか、BMSファイルをアップロードし直してください。<br/>\";\n\t\techo \"無効な譜面のmd5 : \".$md5;\n\t\texit();\n\t}\n\t\n\t$lgnote = $data[\"lgkey\"];\n\t$lnlength = $data[\"lnlength\"];\n\t$measure_len = $data[\"measure_length\"];\n\n\t// draw header data:\n\t$insane_difficulty = new DifficultyTable();\n\t\n\techo $insane_difficulty->getDifficulty('./table/insane/', $md5).\" \";\n\techo '<span class=\"title\">'.$data[\"TITLE\"].\"</span> \";\n\techo $data[\"GENRE\"].\", \";\n\techo $data[\"ARTIST\"].\", \";\n\techo $data[\"notes\"].\"notes, \";\n\techo $data[\"BPM\"].\"bpm\";\n\techo '<a href=\"https://twitter.com/share\" class=\"twitter-share-button\" data-lang=\"ja\">ツイート</